import React from 'react';
import {
    Show,
    SimpleShowLayout,
    TextField,
    DateField,
    BooleanField,
    SingleFieldList,
    ChipField,
    ReferenceField,
    ArrayField,
    useListContext
} from 'react-admin';

import Chip from "@material-ui/core/Chip";
import Avatar from "@material-ui/core/Avatar";


const PhotoChip = () => {
    const { ids, data, basePath } = useListContext();
    return (
        <div>
            {ids.map(id =>
                <Chip record={data[id]} source={'title'}/>
            )}
        </div>
    );
};

const CompetitionShow = (props) => {
    return (
        <Show {...props}>
            <SimpleShowLayout>
                <TextField source='id' />
                <TextField source='name' />
                <TextField source='creator.email' />
                <DateField showTime={true} source='deadline' />
                <BooleanField label = 'Visibility' source='currentVisibility'/>

                <ArrayField
                    label='Photos in competition'
                    source='photoList'>
                    <SingleFieldList>
                        <ReferenceField source='_id' reference='photos'>
                            
                            <PhotoChip/>
                        </ReferenceField>
                    </SingleFieldList>
                </ArrayField>

                <ArrayField
                    label='VIP members'
                    source='VIP'>
                    <SingleFieldList>
                        <ReferenceField source='_id' reference='puser'>
                            <ChipField source='email'/>
                        </ReferenceField>
                    </SingleFieldList>
                </ArrayField>

            </SimpleShowLayout>
        </Show>
    )
};

export default CompetitionShow;
