import customKeysDataProvider from 'ra-data-rest-client';
import {fetchUtils} from "react-admin";

const httpClient = (url, options = {}) => {
    if (!options.headers) {
        options.headers = new Headers({ Accept: '*/*' });
    }
    const token = JSON.parse(localStorage.getItem('token'));
    options.headers.set('Authorization', `Bearer ${token}`);
    return fetchUtils.fetchJson(url, options);
};

const customKeysHash = {
    'products': '_id',
    'orders': '_id',
    'users': '_id'
}

const dataProvider = customKeysDataProvider('http://localhost:5000', customKeysHash, {}, httpClient);

const myDataProvider = {
    ...dataProvider,
    create: (resource, params) => {
        if (resource !== 'products' || !params.models.product.productImage) {
            // fallback to the default implementation
            return dataProvider.create(resource, params);
        }

        let formData = new FormData();

        formData.append('title', params.models.product.title);
        formData.append('year', params.models.product.year);
        formData.append('author', params.models.product.author);
        formData.append('desc', params.models.product.desc);
        formData.append('productImage', params.models.product.productImage);

        return httpClient(`http://localhost:5000/products`, {
            method: 'POST',
            body: formData,
        }).then(({ json }) => ({
            data: { ...params.data, id: json.id },
        }));
    }
};

export default myDataProvider;
