import React from 'react';
import {List, Datagrid, TextField, NumberField, ShowButton} from 'react-admin';

const PostTitle = ({ record }) => {
    return <span>{record ? `"${record._id}"` : ''}</span>;
};

const OrderList = (props) => {
    return <List title = {<PostTitle/>} {...props}>
        <Datagrid>
            <TextField label = 'Title of product' source = 'product.title' />
            <NumberField source = 'quantity' />
            <ShowButton record = {<PostTitle/>} basePath = '/orders'/>
        </Datagrid>
    </List>
};

export default OrderList;
