import React from 'react';
import {
    Edit,
    SimpleForm,
    TextInput,
    DateTimeInput,
    BooleanInput,
    ArrayInput,
    SimpleFormIterator,
    ReferenceInput,
    SelectInput
} from 'react-admin';

const CompetitionEdit = (props) => {
    return (
        <Edit title = 'Edit Competition' {...props}>
            <SimpleForm>
                <TextInput disabled source='id' />
                <TextInput source='name' />
                <TextInput disabled label = 'Creator' source='creator.email' />
                <DateTimeInput source='deadline' />
                <BooleanInput disabled label = 'Visibility' source='currentVisibility'/>

                <ArrayInput label='Photos in competiton' source='photoList'>
                    <SimpleFormIterator>
                        <ReferenceInput source='_id' reference='photos'>

                        </ReferenceInput>
                    </SimpleFormIterator>
                </ArrayInput>

            </SimpleForm>
        </Edit>
    )
};

export default CompetitionEdit;
