import React from 'react';
import {Show, SimpleShowLayout, TextField, NumberField} from 'react-admin';

const OrderShow = (props) => {
    return (
        <Show title = 'Edit Order' {...props}>
            <SimpleShowLayout>
                <TextField source='product._id' />
                <TextField source='product.title' />
                <NumberField source='product.year' />
                <TextField source='product.author' />
                <NumberField label = 'quantity' source='quantity' />
            </SimpleShowLayout>
        </Show>
    )
};

export default OrderShow;
