import React from 'react';
import {Show, SimpleShowLayout, TextField, DateField, ImageField, ArrayField, Datagrid,
ReferenceManyField, SingleFieldList, ChipField} from 'react-admin';

const PhotoShow = (props) => {
    return (
        <Show {...props}>
            <SimpleShowLayout>
                <TextField source='id' />
                <TextField source='title' />
                <TextField source='description' />
                <ImageField label='Image' source='ownImage'/>
                <TextField label = 'Owner' source='ownerID.email'/>
                <DateField showTime={true} label = 'Uploaded at' source='upload'/>
                <ArrayField label='Comments' source='comment'>
                    <Datagrid>
                        <TextField label='User' source='user.email'/>
                        <TextField label='Comment' source='text'/>
                    </Datagrid>
                </ArrayField>
                <ReferenceManyField reference={'categories'} target={'photoList._id'}>
                    <SingleFieldList>
                        <ChipField source={'name'}/>
                    </SingleFieldList>
                </ReferenceManyField>
            </SimpleShowLayout>
        </Show>
    )
};

export default PhotoShow;
