import React from 'react';
import {Create, SimpleForm, TextInput, ImageInput, ImageField, NumberInput} from 'react-admin';

const toDataURL = url => fetch(url)
    .then(response => response.blob())
    .then(blob => new Promise((resolve, reject) => {
        const reader = new FileReader()
        reader.onloadend = () => resolve(reader.result)
        reader.onerror = reject
        reader.readAsDataURL(blob)
    }))

const ProductCreate = (props) => {
    const imageHandler = (e) => {
        const reader = new FileReader();
        reader.onload = () => {
            if (reader.readyState === 2) {
                this.setState({productImage: reader.result});
            }
        }
        reader.readAsDataURL(e.target.files[0]);
    };
    return (
        <Create title = 'New Product' {...props}>
            <SimpleForm>
                <TextInput source='title' />
                <NumberInput source='year' />
                <TextInput source='author' />
                <TextInput multiline label='Description' source='desc' />
                <ImageInput label = 'Image of Product'
                            source='productImage'
                            onChange={toDataURL('productImage')
                                .then(dataUrl => {
                                    console.log('RESULT:', dataUrl)
                                })}>
                    >
                           <ImageField source='src'/>
                </ImageInput>
            </SimpleForm>
        </Create>
    )
};

export default ProductCreate;
