import React from 'react';
import { useListContext, List, TextField, DateField, ReferenceField, EditButton } from 'react-admin';
import { Card, CardActions, CardContent, CardHeader, Avatar } from '@material-ui/core';

const cardStyle = {
    width: 300,
    minHeight: 300,
    margin: '0.5em',
    display: 'inline-block',
    verticalAlign: 'top'
};
const PhotoGrid = () => {
    const { ids, data, basePath } = useListContext();
    return (
        <div style={{ margin: '1em' }}>
            {ids.map(id =>
                <Card key={id} style={cardStyle}>
                    <CardHeader
                        title={<TextField record={data[id]} source="title" />}
                        subheader={<DateField showTime={true} record={data[id]} source="upload" />}
                    />
                    <CardContent>
                        <TextField record={data[id]} source="description" />
                    </CardContent>
                    <CardActions style={{ textAlign: 'right' }}>
                        <EditButton resource="photos" basePath={basePath} record={data[id]} />
                    </CardActions>
                </Card>
            )}
        </div>
    );
};

const PhotoList2 = (props) => (
    <List title="All photos" {...props}>
        <PhotoGrid />
    </List>
);

export default PhotoList2;
