import React from 'react';
import {
    Edit,
    SimpleForm,
    TextInput,
    NumberInput,
    BooleanInput,
    SelectInput,
    ReferenceInput
} from 'react-admin';

const CategoryEdit = (props) => {
    return (
        <Edit title = 'Edit Category' {...props}>
            <SimpleForm>
                <TextInput disabled source='id' />
                <TextInput source='name' />
                <TextInput disabled label = 'Creator' source='creator.email' />
                <NumberInput source='limit' />
                <BooleanInput source='visibility' />
                <ReferenceInput
                    source="photoList"
                    reference='photos'
                    allowEmpty={true}
                    multiple={true}>
                    <SelectInput
                        source='photoList'
                        optionText='title'
                        optionValue='id'
                    />
                </ReferenceInput>
            </SimpleForm>
        </Edit>
    )
};

export default CategoryEdit;
