import React from 'react';
import {Create, SimpleForm, TextInput, NumberInput, SelectInput, ReferenceInput} from 'react-admin';

const choices = [
    { _id: 123, full_name: 'Leo Tolstoi', sex: 'M' },
    { _id: 456, full_name: 'Jane Austen', sex: 'F' },
];

const OrderCreate = (props) => {
    return (
        <Create title = 'New Order' {...props}>
            <SimpleForm>
                <TextInput source='productId' />
                <SelectInput source='productId' choices={choices} optionText='full_name' optionValue='_id'/>
                <NumberInput label = 'quantity' source='quantity' />
                <ReferenceInput
                    source="productId"
                    reference={'products'}
                    allowEmpty={false}
                >
                    <SelectInput
                        source='productId'
                        optionText='products.title'
                        optionValue='products.title'
                    />
                </ReferenceInput>
            </SimpleForm>
        </Create>
    )
};

export default OrderCreate;
